import React, { useEffect, useState } from 'react';
import { fetchData } from './api';

const ItemsList = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getData = async () => {
      try {
        const items = await fetchData();
        setItems(items);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(error);
        setLoading(false);
      }
    };
    getData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error fetching items: {error.message}</p>;

  return (
    <div>
      <h2>Items List</h2>
      <ul>
        {items.map((item) => (
          <li key={item.id}>
            <h2>{item.attributes.Compile}</h2>
            <div>
              {item.attributes.Content && item.attributes.Content.map((contentItem, index) => (
                <div key={index}>
                  {contentItem.type === 'heading' && (
                    <p>{contentItem.children.map(child => child.text).join(' ')}</p>
                  )}
                </div>
              ))}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemsList;
